import React, {Component} from 'react';
import { Container, Col } from 'react-bootstrap';

class Detail extends Component {
    render(){
        return(
            <Container className="mt-4">
                <p style={{fontSize: '12px'}}>Kategori / Komik / Naruto</p>

                <Col className="d-flex">
                    <div className="box-img mr-5" style={{height: '400px'}}>
                        <img src="img/naruto.jpg" alt="img" height="100%"></img>
                    </div>

                    <div className="box-detail">
                        <h2>Naruto Shippuden</h2>
                        <h5 className="text-secondary">Rp 50.000   </h5>
                        <br></br>
                        <div className="min-plus">
                            <button className="btn btn-secondary py-2 mr-2">-</button>
                            <input type="text py-2" style={{width: '50px',padding: '5px 10px', borderRadius: '5px', border: '1px solid #aaa' }} value="01"></input>
                            <button className="btn btn-secondary py-2 ml-2">+</button>
                        </div>
                        <Link to="/cart">
                            Add Cart
                        </Link>
                    </div>
                </Col>
            </Container>
        )
    }
}

export default Detail;
